import { SET_ALERT , REMOVE_ALERT }  from '../actions/types'

const initiaState = [];

export default function(state = initiaState, action){
    switch(action.type){
        case SET_ALERT:
            return [...state,action.payload];
        case REMOVE_ALERT:
            return state.filter(alert => alert.id !== payload);
        default:
            return state;
    }
}
